function [Q R] = prob4(A)  [n n] = size(A);    for j = 1 : n    for i = 1 : j - 1      R(i, j) = Q(:, i)' * A(:, j);          endfor        s = 0;    for i = 1 : j - 1      s += R(i, j) * Q(:, i);    endfor        aux = A(:, j) - s;        s = 0;    for i = 1 : n      s += aux(i) ^ 2;    endfor        s = sqrt(s);        R(j, j) = s;        Q(:, j) = aux / R(j, j);    endforendfunction