in = fopen('magic.in', 'r');# citire dimensiune matriceN = fscanf(in, '%d', 1);# citire elemente matricefor i = 1 : N  for j = 1 : N    M(i, j) = fscanf(in, '%d', 1);  endforendforfor i = 1 : N  lin(i) = 0;  for j = 1 : N    lin(i) += M(i,j);  endforendfordisp('Suma pe linii este: ');disp(lin);for j = 1 : N  col(j) = 0;  for i = 1 : N    col(j) += M(i,j);   endforendfordisp('Suma pe coloane este: ');disp(col);diag_p = 0;diag_s = 0;for i = 1 : N  diag_p += M(i,j);  diag_s += M(i, N - i + 1);endfordisp('Suma elementelor de pe diagonala principala este: ');disp(diag_p);disp('Suma elementelor de pe diagonala secundara este: ');disp(diag_s);linie = 1;coloana = 1;for i = 1 : N - 1  if lin(i) != lin(i + 1)    linie = 0;  endif  if col(i) != col(i + 1)    coloana = 0;  endifendfor#disp(linie);#disp(coloana);if diag_p == diag_s && linie == 1 && coloana == 1  disp('Este patrat magic');else  disp('Nu este patrat magic');endif    