function y = prob5(numefis, sir)  in = fopen('date.txt', 'r');    count = 0;    while 1    lin = fgetl(in);    if ~ischar(lin), break, end    disp(lin);        ind = findstr(lin, sir);        #disp(ind);        disp(length(ind));        count += length(ind);      endwhile     disp('Numarul total de aparitii este: ');   disp(count);     fclose(in);endfunction