function [L U] = prob2(A)    [n n] = size(A);    L = eye(n);  U = zeros(n);    U(1, 1 : n) = A(1, 1 : n);    for p = 1 : n    for j = p : n          s = 0;      for k = 1 : p - 1        s += L(p, k) * U(k, j);      endfor            U(p, j) = A(p, j) - s;        endfor        for i = p + 1 : n            s = 0;      for k = 1 : p - 1        s += L(i, k) * U(k, p);      endfor            L(i, p) = (A(i, p) - s) / U(p, p);          endfor      endfor  endfunction