A = input('Introduceti matricea: ');[n n] = size(A);# extragere submatriceA1 = A([1 : n / 2], [1 : n / 2]);A2 = A([n / 2 + 1 : n], [1 : n / 2]);A3 = A([1 : n / 2], [n / 2 + 1, n]);A4 = A([n / 2 + 1 : n], [n / 2 + 1, n]);inv_A1 = inv(A1);inv_A4 = inv(A4);X1 = inv(A1 - A3 * inv_A4 * A2);X2 = -inv_A4 * A2 * X1;X4 = inv(A4 - A2 * inv_A1 * A3);X3 = -inv_A1 * A3 * X4;# formare matrice XX([1 : n / 2], [1 : n / 2]) = X1;X([n / 2 + 1 : n], [1 : n / 2]) = X2;X([1 : n / 2], [n / 2 + 1 : n]) = X3;X([n / 2 + 1 : n], [n / 2 + 1 : n]) = X4;disp(X);