function x = sor2(A, b, x0, w, tol, maxiter)[n n] = size(A);x = x0;for k = 1 : maxiter  pred = x;    for i = 1 : n    %calculez x(i)    suma1 = 0;  for j = 1 : i - 1        suma1 += A(i, j) * pred(j);  endfor    suma2 = 0;  for j = i + 1 : n    suma2 += A(i, j) * pred(j);  endfor    x(i) = ((b(i) - suma1 - suma2) / A(i, i)) * w + (1 - w) * pred(i);    endforendforendfunction